mapper.select:
select

from merchant_settle_order
where 1 = 1


	and merchant_id = #{merchantId,jdbcType=INTEGER}



	and status = #{status,jdbcType=INTEGER}




	and order_number like #{orderNumber}



	and create_time >=
	#{cBeginTime}



	and create_time <=
	#{cEndTime}



	and pay_time >=
	#{sBeginTime}



	and pay_time <=
	#{sEndTime}


order by create_time desc

SELECT merchant_id as merchantId, count(1) as settleNumber, sum(postal_amount) as settleAmount, sum(service_amount) as settleServiceAmount from merchant_settle_order where status = 0
and create_time >= #{beginTime}
and create_time <= #{endTime}
group by merchant_id
